INSERT INTO public.publisher (id, name, description, deleted) VALUES (3, 'Gramedia Pustaka Utama', 'Salah satu penerbit terbesar di Indonesia yang menerbitkan berbagai genre buku, termasuk karya-karya bestseller nasional', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (4, 'Scribner', 'Penerbit Amerika Serikat yang merupakan bagian dari Simon & Schuster dan menerbitkan banyak karya Stephen King', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (5, 'Doubleday', 'Penerbit Amerika Serikat yang menerbitkan beberapa karya awal Stephen King termasuk The Shining', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (6, 'Gagas Media', 'Penerbit Indonesia yang fokus pada buku-buku fiksi populer dan non-fiksi untuk pembaca muda', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (7, 'Harper & Row', 'Penerbit Amerika Serikat yang menerbitkan edisi bahasa Inggris dari banyak karya Gabriel García Márquez sebelum bergabung menjadi HarperCollins', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (8, 'Editorial Sudamericana', 'Penerbit Argentina yang menerbitkan banyak karya Gabriel García Márquez dalam bahasa Spanyol aslinya', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (9, 'Media Kita', 'Penerbit Indonesia yang fokus pada buku-buku fiksi populer dan karya-karya penulis muda Indonesia', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (10, 'Grove Press', 'Penerbit Amerika Serikat yang berfokus pada karya-karya sastra terjemahan, termasuk banyak karya penulis Jepang seperti Banana Yoshimoto', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (11, 'Kadokawa Shoten', 'Penerbit Jepang yang menerbitkan banyak karya sastra Jepang kontemporer termasuk beberapa karya Banana Yoshimoto dalam bahasa aslinya', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (12, 'Doubleday', 'Penerbit Amerika Serikat yang merupakan bagian dari Penguin Random House, terkenal menerbitkan karya-karya Dan Brown termasuk The Da Vinci Code', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (13, 'Editum', 'Penerbit independen Indonesia yang fokus pada penerbitan karya sastra dan puisi Indonesia', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (14, 'Random House', 'Penerbit buku internasional yang berbasis di Amerika Serikat dan merupakan bagian dari Penguin Random House, menerbitkan berbagai genre buku termasuk karya sastra', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (15, 'Hamish Hamilton', 'Penerbit Inggris yang merupakan bagian dari Penguin Books, terkenal menerbitkan karya-karya fiksi sastra berkualitas tinggi', false);
INSERT INTO public.publisher (id, description, name, deleted) VALUES (16, 'Simon & Schuster adalah salah satu penerbit buku terbesar di dunia yang didirikan pada tahun 1924 oleh Richard L. Simon dan M. Lincoln Schuster.', 'Simon & Schuster', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (17, 'Bloomsbury Publishing', 'Penerbit independen asal Inggris yang didirikan pada tahun 1986, terkenal karena menerbitkan seri Harry Potter di Inggris dan berbagai negara di luar Amerika Serikat', false);
INSERT INTO public.publisher (id, name, description, deleted) VALUES (18, 'Scholastic Corporation', 'Penerbit multinasional asal Amerika yang fokus pada buku pendidikan dan anak-anak, memegang hak penerbitan seri Harry Potter di Amerika Serikat', false);
INSERT INTO public.category (code, name, description, deleted) VALUES ('NOVEL', 'Novel', 'Karya fiksi prosa panjang yang mengandung rangkaian cerita kehidupan seseorang dengan orang-orang di sekelilingnya', false);
INSERT INTO public.category (code, name, description, deleted) VALUES ('CERPEN', 'Cerita Pendek', 'Karya fiksi prosa yang lebih pendek dari novel dan biasanya dapat dibaca dalam sekali duduk', false);
INSERT INTO public.category (code, name, description, deleted) VALUES ('SASTRA_JEPANG', 'Sastra Jepang', 'Karya sastra yang ditulis oleh penulis Jepang atau berlatar belakang budaya dan masyarakat Jepang', false);
INSERT INTO public.category (code, name, description, deleted) VALUES ('THRILLER', 'Thriller', 'Karya fiksi yang dirancang untuk membuat pembaca tegang dan bersemangat melalui penggunaan suspense, ketegangan, dan plot yang cepat', false);
INSERT INTO public.category (code, name, description, deleted) VALUES ('PUISI', 'Puisi', 'Bentuk karya sastra yang menggunakan bahasa yang dipadatkan, dipersingkat, dan diberi irama dengan bunyi yang padu', false);
INSERT INTO public.category (code, name, description, deleted) VALUES ('SASTRA_INDONESIA', 'Sastra Indonesia', 'Karya sastra yang ditulis dalam bahasa Indonesia atau oleh penulis Indonesia', false);
INSERT INTO public.category (code, name, description, deleted) VALUES ('SASTRA_INDIA', 'Sastra India', 'Karya sastra yang ditulis oleh penulis India atau berlatar belakang budaya dan masyarakat India', false);
INSERT INTO public.category (code, name, description, deleted) VALUES ('SCIENCE_POPULAR', 'Sains Populer', 'Kategori ini mencakup buku - buku yang menyajikan konsep ilmiah dalam format yang mudah dipahami oleh pembaca umum', false);
INSERT INTO public.category (code, deleted, description, name) VALUES ('BIOGRAPHY', false, 'Kategori biografi mencakup buku-buku yang menceritakan kisah hidup seseorang yang ditulis oleh orang lain.', 'Biografi');
INSERT INTO public.category (code, name, description, deleted) VALUES ('FANTASI', 'Fantasi', 'Karya fiksi yang mengandung elemen-elemen magis, supernatural, atau dunia alternatif yang tidak ada dalam realitas', false);
INSERT INTO public.category (code, name, description, deleted) VALUES ('FIKSI_ANAK', 'Fiksi Anak', 'Karya sastra yang ditujukan untuk pembaca anak-anak dan remaja, biasanya dengan tema dan bahasa yang sesuai untuk kelompok usia tersebut', false);
INSERT INTO public.category (code, name, description, deleted) VALUES ('TEKNOLOGI', 'Teknologi', 'Buku yang membahas tentang perkembangan, sejarah, dampak, dan masa depan teknologi dalam berbagai aspek kehidupan', false);
INSERT INTO public.category (code, name, description, deleted) VALUES ('SEJARAH', 'Sejarah', 'Karya non-fiksi yang mengkaji peristiwa masa lalu berdasarkan bukti dan analisis', false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (1, 'Laskar Pelangi', 'Novel pertama dari tetralogi Laskar Pelangi yang mengisahkan perjuangan sepuluh anak dari keluarga miskin di Belitung untuk mendapatkan pendidikan di SD Muhammadiyah. Kisah ini didasarkan pada pengalaman masa kecil Andrea Hirata sendiri.', 529, 2005, 1, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (2, 'Sang Pemimpi', 'Novel kedua dari tetralogi Laskar Pelangi yang mengisahkan kehidupan Ikal dan kawan-kawannya saat bersekolah di SMA Negeri Manggar, serta impian mereka untuk melanjutkan pendidikan ke Sorbonne, Prancis.', 292, 2006, 1, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (3, 'Supernova: Kesatria, Putri, dan Bintang Jatuh', 'Novel pertama dari seri Supernova yang mengisahkan tentang Dhimas dan Ruben, sepasang penulis yang menciptakan cerita fiksi yang ternyata mempengaruhi kehidupan nyata tiga karakter lainnya: Ferre, Rana, dan Diva. Novel ini menggabungkan tema-tema sains, spiritualitas, dan hubungan antarmanusia.', 342, 2001, 2, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (4, 'Filosofi Kopi', 'Kumpulan cerita pendek yang mengeksplorasi berbagai aspek kehidupan manusia melalui metafora kopi dan filosofi di baliknya. Cerita utama mengisahkan tentang Ben dan Jody, dua sahabat yang mendirikan kedai kopi dan perjalanan Ben mencari resep kopi sempurna.', 155, 2006, 2, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (5, 'Bumi', 'Novel pertama dari serial Bumi yang mengisahkan petualangan seorang gadis bernama Raib yang memiliki kemampuan menghilang. Bersama dua temannya, Ali dan Seli, Raib menemukan dunia paralel bernama Klan Bulan dan terlibat dalam petualangan luar biasa untuk menyelamatkan dunia.', 440, 2014, 3, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (6, 'Hujan', 'Novel yang mengisahkan kisah cinta antara Lail dan Esok yang dimulai dari bencana gunung meletus dan gempa dahsyat yang menghancurkan kota mereka. Dalam perjalanan hidupnya, Lail belajar tentang persahabatan, pengorbanan, dan arti kebahagiaan yang sesungguhnya.', 320, 2016, 3, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (7, 'The Shining', 'Novel horor psikologis yang mengisahkan tentang Jack Torrance, seorang penulis dan pemulih alkoholik yang menjadi penjaga musim dingin di hotel terpencil bernama Overlook Hotel bersama keluarganya. Seiring waktu, kekuatan supernatural hotel mulai mempengaruhi Jack dan mengancam keluarganya, sementara putranya Danny yang memiliki kemampuan psikis "the shining" melihat visi-visi mengerikan.', 447, 1977, 5, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (8, 'It', 'Novel horor epik yang mengisahkan tentang sekelompok anak-anak yang dikenal sebagai "The Losers Club" yang menghadapi entitas jahat yang menyamar sebagai badut bernama Pennywise di kota Derry, Maine. Cerita berpindah antara dua periode waktu, saat karakter utama masih anak-anak dan kemudian saat mereka dewasa, ketika mereka harus kembali ke kota asal mereka untuk menghadapi kembali teror dari masa kecil mereka.', 1138, 1986, 4, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (9, 'Kambing Jantan: Sebuah Catatan Harian Pelajar Bodoh', 'Buku pertama Raditya Dika yang merupakan kumpulan tulisan dari blognya, mengisahkan pengalaman-pengalaman lucu dan absurd selama ia menjalani studi di Adelaide, Australia. Buku ini menceritakan keseharian Dika sebagai mahasiswa Indonesia di luar negeri dengan gaya humor khasnya.', 264, 2005, 6, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (10, 'Cinta Brontosaurus', 'Buku kedua Raditya Dika yang mengisahkan pengalaman-pengalaman cintanya yang konyol dan tidak biasa. Dengan gaya humor yang khas, Dika menceritakan berbagai kisah cinta yang gagal dan pembelajaran yang ia dapatkan dari pengalaman tersebut.', 248, 2006, 6, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (11, 'Cien años de soledad', 'Novel epik yang mengisahkan tujuh generasi keluarga Buendía di kota fiksi Macondo. Karya ini menggabungkan elemen realisme magis dengan sejarah Kolombia, mengeksplorasi tema-tema kesunyian, pengulangan waktu, dan takdir. Novel ini dianggap sebagai mahakarya García Márquez dan salah satu karya sastra terpenting abad ke-20.', 417, 1967, 8, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (12, 'El amor en los tiempos del cólera', 'Novel yang mengisahkan kisah cinta Florentino Ariza dan Fermina Daza yang berlangsung selama lebih dari 50 tahun. Setelah ditolak oleh Fermina yang memilih menikah dengan dokter Juvenal Urbino, Florentino tetap setia menunggu selama setengah abad hingga kematian suami Fermina memberinya kesempatan kedua. Novel ini mengeksplorasi tema-tema cinta, waktu, usia tua, dan kematian dengan latar belakang masyarakat Karibia pada pergantian abad ke-20.', 348, 1985, 7, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (13, 'Garis Waktu', 'Novel debut Fiersa Besari yang mengisahkan tentang perjalanan hidup dan cinta seorang tokoh utama bernama Fajri. Melalui rangkaian cerita pendek yang disusun seperti potongan-potongan kenangan, novel ini mengeksplorasi tema waktu, cinta, kehilangan, dan bagaimana semua pengalaman tersebut membentuk seseorang. Garis Waktu menggabungkan prosa dan puisi dengan cara yang unik untuk menyampaikan emosi dan refleksi tentang kehidupan.', 204, 2016, 9, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (14, 'Konspirasi Alam Semesta', 'Novel kedua Fiersa Besari yang mengisahkan pertemuan dan perjalanan cinta Juang Astrajingga dan Senja. Melalui perjalanan mereka berdua, novel ini mengeksplorasi tema-tema tentang takdir, kebetulan, dan bagaimana alam semesta seolah berkonspirasi untuk mempertemukan dua orang yang ditakdirkan bersama. Dengan latar belakang perjalanan keliling Indonesia, novel ini juga menggambarkan keindahan alam dan budaya nusantara.', 280, 2017, 9, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (15,'Kitchen', 'Novel pendek yang mengisahkan tentang Mikage Sakurai, seorang gadis muda yang baru saja kehilangan neneknya, satu-satunya keluarga yang ia miliki. Setelah kematian neneknya, Mikage diundang untuk tinggal bersama Yuichi Tanabe, teman neneknya, dan ibunya Eriko, yang sebenarnya adalah ayah Yuichi yang telah menjalani operasi perubahan gender setelah kematian istrinya. Novel ini mengeksplorasi tema-tema kesepian, kehilangan, dan transformasi melalui hubungan antar karakter dan hubungan mereka dengan dapur, yang menjadi simbol kehangatan dan kehidupan. "Kitchen" juga mencakup cerita pendek "Moonlight Shadow" yang mengeksplorasi tema-tema serupa tentang kehilangan dan proses penyembuhan.', 152, 1988, 11, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (16, 'Goodbye Tsugumi', 'Novel yang mengisahkan hubungan antara dua sepupu, Maria dan Tsugumi, selama musim panas terakhir mereka bersama di sebuah kota pesisir sebelum keluarga mereka pindah. Tsugumi, yang telah sakit-sakitan sejak lahir, memiliki kepribadian yang kompleks - cantik namun sinis, kejam namun memikat. Melalui sudut pandang Maria, novel ini mengeksplorasi tema-tema persahabatan, kematian, dan perubahan, serta bagaimana kesadaran akan kefanaan hidup dapat memengaruhi cara seseorang menjalani hidupnya. Dengan latar belakang pemandangan laut yang indah, "Goodbye Tsugumi" adalah potret yang mendalam tentang masa remaja dan transisi ke kedewasaan.', 186, 1989, 10, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (17,'The Da Vinci Code', 'Novel thriller yang mengikuti Robert Langdon, seorang profesor simbologi dari Harvard, dan Sophie Neveu, seorang kriptolog Prancis, dalam pencarian mereka untuk memecahkan pembunuhan misterius di Museum Louvre. Investigasi mereka mengungkap konspirasi yang melibatkan Priory of Sion dan Opus Dei, serta rahasia yang berkaitan dengan Holy Grail dan Yesus Kristus. Novel ini menggabungkan elemen sejarah, seni, kode, dan simbolisme agama, menciptakan kontroversi dengan teorinya tentang Yesus yang menikah dengan Maria Magdalena dan memiliki keturunan. Meskipun banyak dikritik oleh sejarawan dan teolog atas akurasi historisnya, novel ini menjadi fenomena global dan salah satu buku terlaris sepanjang masa.', 489, 2003, 12, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (18,'Angels & Demons', 'Novel thriller yang merupakan prekuel dari "The Da Vinci Code", meskipun diterbitkan lebih dulu. Cerita ini mengikuti Robert Langdon dalam petualangannya pertama kali, ketika ia dipanggil ke CERN di Swiss untuk menyelidiki pembunuhan seorang fisikawan yang tubuhnya ditemukan dengan simbol Illuminati yang dibakar di dadanya. Langdon kemudian terlibat dalam perlombaan melawan waktu di Vatikan untuk menghentikan ancaman bom antimatter yang disembunyikan oleh Illuminati, sebuah persaudaraan rahasia kuno yang bermusuhan dengan Gereja Katolik. Novel ini menggabungkan elemen sejarah, sains, agama, dan seni, mengeksplorasi konflik antara sains dan agama serta sejarah persaudaraan Illuminati.', 616, 2000, 12, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (19, 'Hujan Bulan Juni', 'Kumpulan puisi Sapardi Djoko Damono yang berisi puisi-puisi tentang cinta, kehidupan, dan refleksi filosofis. Puisi yang menjadi judul buku ini, "Hujan Bulan Juni", adalah salah satu karya paling terkenal Sapardi yang menggunakan metafora hujan di bulan Juni (musim kemarau di Indonesia) untuk menggambarkan paradoks dan kompleksitas cinta. Kumpulan ini menampilkan gaya khas Sapardi dengan bahasa yang sederhana namun kaya makna, metafora alam yang indah, dan kemampuannya mengekspresikan perasaan dan pemikiran yang mendalam melalui kata-kata yang minimal. Puisi-puisi dalam buku ini telah memengaruhi banyak generasi pembaca dan penyair Indonesia.', 100, 1994, 3, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (20, 'Perahu Kertas', 'Kumpulan puisi Sapardi Djoko Damono yang diterbitkan pada tahun 1983. Buku ini berisi puisi-puisi yang menampilkan gaya khas Sapardi dengan bahasa yang sederhana namun kaya makna dan metafora yang mendalam. Judul "Perahu Kertas" sendiri mengacu pada salah satu puisi terkenal dalam kumpulan ini yang menggunakan metafora perahu kertas untuk menggambarkan kerapuhan dan perjalanan hidup manusia. Puisi-puisi dalam kumpulan ini mengeksplorasi tema-tema universal seperti cinta, kehidupan, kematian, dan eksistensi manusia, yang disampaikan melalui imaji-imaji alam dan kehidupan sehari-hari. "Perahu Kertas" dianggap sebagai salah satu karya penting dalam khazanah puisi modern Indonesia dan telah memengaruhi banyak penyair generasi berikutnya.', 88, 1983, 3, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (21,'The God of Small Things', 'Novel debut Arundhati Roy yang memenangkan Booker Prize 1997. Novel ini mengisahkan kehidupan keluarga Ipe di Kerala, India, berfokus pada sepasang anak kembar, Rahel dan Estha, serta peristiwa-peristiwa tragis yang terjadi pada tahun 1969. Dengan struktur non-linear yang bergerak maju mundur dalam waktu, novel ini mengeksplorasi tema-tema kompleks seperti sistem kasta, politik komunis, tabu sosial, dan "Hukum Cinta" yang menentukan "siapa yang boleh dicintai, bagaimana, dan seberapa banyak." Gaya bahasa Roy yang kaya, puitis, dan eksperimental, dengan permainan kata dan frasa yang berulang, menciptakan narasi yang mendalam tentang bagaimana "hal-hal kecil" dalam kehidupan dapat memiliki dampak besar pada nasib manusia. Novel ini juga mengkritik struktur sosial India, kolonialisme, dan patriarki, sambil menggambarkan keindahan dan kompleksitas kehidupan di Kerala.', 340, 1997, 14, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (22, 'The Ministry of Utmost Happiness', 'Novel kedua Arundhati Roy yang diterbitkan setelah jeda 20 tahun dari karya fiksi pertamanya. Novel ini mengikuti perjalanan Anjum, seorang waria (hijra) yang meninggalkan komunitas hijra-nya untuk membangun kehidupan baru di sebuah pemakaman, dan Tilo, seorang arsitek yang terlibat dalam perjuangan kemerdekaan Kashmir. Melalui kehidupan karakter-karakter yang terpinggirkan ini, Roy mengeksplorasi berbagai aspek sejarah India kontemporer, termasuk konflik Kashmir, gerakan Naxalite, kebangkitan Hindu nasionalis, dan dampak kapitalisme global. Novel ini menggabungkan narasi personal dengan kritik sosial-politik yang tajam, menggambarkan India yang kompleks dan penuh kontradiksi. Dengan gaya prosa yang kaya dan puitis, Roy menciptakan mosaik cerita yang saling terhubung yang menantang pembaca untuk memikirkan kembali gagasan tentang kebahagiaan, keluarga, dan identitas dalam dunia yang terpecah-belah.', 464, 2017, 15, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (23, 'Harry Potter and the Philosopher''s Stone', 'Buku pertama dalam seri Harry Potter yang memperkenalkan Harry Potter, seorang anak yatim piatu yang mengetahui bahwa ia adalah penyihir pada ulang tahunnya yang ke-11 ketika ia menerima undangan untuk menghadiri Sekolah Sihir Hogwarts. Buku ini mengikuti tahun pertama Harry di Hogwarts saat ia mulai mempelajari sihir, membuat teman baru seperti Ron Weasley dan Hermione Granger, dan menghadapi Lord Voldemort, penyihir jahat yang membunuh orang tuanya. Harry dan teman-temannya menyelidiki misteri Batu Bertuah, sebuah artefak alkimia yang dapat mengubah logam menjadi emas dan menghasilkan Elixir Kehidupan yang membuat pemakainya abadi. Dengan perpaduan antara petualangan, misteri, humor, dan tema-tema tentang persahabatan, keberanian, dan pilihan yang mendefinisikan siapa kita, buku ini meletakkan dasar untuk dunia sihir yang kompleks dan memikat yang akan dikembangkan dalam buku-buku selanjutnya.', 223, 1997, 17, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (24, 'Harry Potter and the Chamber of Secrets', 'Buku kedua dalam seri Harry Potter yang mengikuti tahun kedua Harry di Sekolah Sihir Hogwarts. Setelah liburan musim panas yang menyebalkan bersama keluarga Dursley, Harry kembali ke Hogwarts namun diperingatkan oleh peri rumah bernama Dobby bahwa bahaya mengancam sekolah. Tak lama setelah kedatangannya, pesan misterius mulai muncul di dinding kastil, mengumumkan bahwa "Kamar Rahasia telah dibuka" dan "musuh sang pewaris, waspadalah." Saat siswa-siswa keturunan Muggle (non-penyihir) mulai diserang dan dibatu-kan oleh kekuatan misterius, Harry dicurigai sebagai pelakunya karena kemampuannya berbicara dengan ular (Parseltongue). Bersama Ron dan Hermione, Harry berusaha mengungkap misteri Kamar Rahasia, identitas pewarisnya, dan monster yang bersembunyi di dalamnya. Buku ini memperdalam mitologi dunia sihir dengan mengeksplorasi sejarah Hogwarts, prasangka darah murni, dan masa lalu Lord Voldemort, sambil mengembangkan tema-tema tentang identitas, warisan, dan kekuatan pilihan atas takdir.', 251, 1998, 18, false);
INSERT INTO public.book (id, title, description, pages, publishing_year, publisher_id, deleted) VALUES (25, 'The Innovators: How a Group of Hackers, Geniuses, and Geeks Created the Digital Revolution', 'The Innovators adalah sebuah karya komprehensif yang mengisahkan sejarah revolusi digital dan orang-orang di baliknya. Walter Isaacson dengan teliti menelusuri evolusi komputer dan internet, dimulai dari Ada Lovelace di abad ke-19 yang menuliskan konsep pemrograman komputer pertama, hingga era modern dengan tokoh-tokoh seperti Bill Gates, Steve Jobs, dan Tim Berners-Lee. Buku ini tidak hanya menceritakan biografi para inovator secara terpisah, tetapi juga mengungkap bagaimana kolaborasi antara para visioner, insinyur, dan wirausahawan telah mendorong kemajuan teknologi. Isaacson mematahkan mitos "penemu tunggal" dan menunjukkan bahwa inovasi terbesar seringkali muncul dari kerja tim dan perpaduan antara seni dan sains. Dengan menggabungkan riset mendalam dan narasi yang mengalir, buku ini memberikan wawasan berharga tentang bagaimana kreativitas kolektif telah membentuk dunia digital yang kita kenal saat ini, serta pelajaran penting tentang inovasi yang dapat diterapkan di berbagai bidang.', 542, 2014, 16, false);
